\begin{abstract}

We develop a class of soliton solution of {\it linear} Schr\"odinger equation without external potential. The quantum probability density generates its own boundary inside which there is internal vibration whose wave number is determined by the velocity of the particle as firstly conjectured by de Broglie. Assuming resonance of the internal vibration will lead to quantization of particle's momentum in term of wave number of the envelope quantum probability density. We further show that the linearity of the Schr\"odinger equation allows us to have non-interacting many solitons solution through superposition, each describing a particle with equal mass. 

\end{abstract}

\begin{equation}
p=\hbar k,
\label{de Broglie relation}
\end{equation}

\begin{equation}
i\hbar\partial_t\psi(q;t)=-\frac{\hbar^2}{2m}\partial_q^2\psi(q;t).
\label{Schroedinger equation}
\end{equation}

\begin{eqnarray}
m\frac{dv}{dt}=-\partial_qU,\hspace{2mm}\partial_t\rho+\partial_q(v\rho)=0.
\label{Madelung fluid}
\end{eqnarray}

\begin{equation}
U(q)= -\frac{\hbar^2}{2m}\frac{\partial_q^2R}{R},
\label{quantum potential}
\end{equation}

\begin{equation}
v(q)=\partial_qS/m.
\label{velocity field}
\end{equation}

\begin{equation}
\rho(q)=\frac{1}{Z}\exp\big(-U(q)/T\big),
\label{canonical QPD}
\end{equation}

\begin{equation}
\partial_q^2U=\frac{1}{2T}(\partial_qU)^2+\frac{4mT}{\hbar^2}U. 
\label{NPDE for U}
\end{equation}

\begin{equation}
\partial_qu=\frac{1}{2T}u^2+\frac{4mT}{\hbar^2}U.
\label{blowing-up NPDE for u}
\end{equation}

\begin{equation}
\partial_q\tilde{u}=\frac{1}{2T}\tilde{u}^2+\frac{4mT}{\hbar^2}X, 
\label{blowing-up for inferior solution}
\end{equation}

\begin{equation}
\tilde{u}(q)=a\tan(bq), \hspace{2mm}a=\frac{2T}{\hbar}\sqrt{2mX}, \hspace{2mm}b=\frac{1}{\hbar}\sqrt{2mX}.  
\label{inferior solution}
\end{equation}

\begin{eqnarray}
\lim_{T\rightarrow 0}U(q;T)=U_0(q),\hspace{2mm}\lim_{T\rightarrow 0}\rho(q;T)=\rho_0(q).
\label{QPD and QP at vanishing temperature}
\end{eqnarray}

\begin{equation}
-\frac{\hbar^2}{2m}\partial_q^2R_0=U_cR_0=\bar{U}_0R_0,
\label{stationary Schroedinger equation 1}
\end{equation}

\begin{equation}
R_0(q)=A_0\cos(k_0 q),
\label{localized-stationary QPD}
\end{equation}

\begin{equation}
k_0=\sqrt{2m\bar{U}_0/\hbar^2}. 
\label{wave number vs average quantum potential}
\end{equation}

\begin{eqnarray}
\rho(q;t)=\rho_0(q-v_ct;0)=A_0^2\cos^2(k_0q-\omega_0 t),
\label{stationary-moving quantum probability density}
\end{eqnarray}

\begin{eqnarray}
\langle E\rangle\equiv \int_{-L_0}^{L_0} dq\hspace{1mm}\psi^*(q)\Big(-\frac{\hbar^2}{2m}\partial_q^2\Big)\psi(q)\hspace{10mm}\nonumber\\
=\int_{-L_0}^{L_0} dq\hspace{1mm}\Big(-\frac{\hbar^2}{2m}R_0\partial_q^2R_0+\frac{1}{2m}R_0^2(\partial_qS)^2
\nonumber\\-\frac{i\hbar}{m}R_0\partial_qR_0\partial_qS-\frac{i\hbar}{2m}R_0^2\partial_q^2S \Big).
\label{calculation of quantum mechanical energy}
\end{eqnarray}

\begin{equation}
\langle E\rangle=\bar{U}_0+\bar{K}_0=\frac{\hbar^2k_0^2}{2m}+\frac{mv_c^2}{2}.
\label{energy decomposition}
\end{equation}

\begin{eqnarray}
\langle p\rangle\equiv\int_{-L_0}^{L_0}dq\hspace{1mm}\psi^*(q)\big(-i\hbar\partial_q\big)\psi(q)=mv_c. 
\label{average momentum}
\end{eqnarray}

\begin{equation}
\psi_s(q;t)=A_0\cos\Big(k_0\Big(q-\frac{\langle p\rangle}{m}t\Big)\Big)\exp\Big(\frac{i}{\hbar}\big(\langle p\rangle q-\langle E\rangle t\big)\Big),
\label{stationary-moving wave function final}
\end{equation}

\begin{equation}
\Delta q=\sqrt{\frac{\hbar^2\pi^2}{2m{\bar U}_0}}.
\label{width vs internal energy}
\end{equation}

\begin{equation}{\bar U}_0=mc^2,
\label{internal energy vs rest mass energy}
\end{equation}

\begin{equation}
\Delta q=\frac{\pi}{\sqrt{2}}\frac{\hbar}{mc}=\frac{\pi}{\sqrt 2}\lambda_C,
\end{equation}

\begin{equation}
k_B=\frac{\langle p\rangle}{\hbar}=\frac{mv_c}{\hbar}. 
\label{de Broglie wave length}
\end{equation}

\begin{equation}
\langle p\rangle=mv_c=n\hbar k_0, \hspace{2mm}n=\pm 1,\pm 2,\dots. 
\label{principle of phase resonance}
\end{equation}

\begin{eqnarray}
\psi_{ms}(q;t)=A_1\cos\Big(k_1\big(q-v_1t\big)\Big)\exp\Big(\frac{i}{\hbar}\big(mv_1 q-E_1 t\big)\Big)\nonumber\\
+A_2\cos\Big(k_2\big(q-v_2t\big)\Big)\exp\Big(\frac{i}{\hbar}\big(mv_2 q-E_2 t\big)\Big). 
\label{two solitons solutions}
\end{eqnarray}

\begin{equation}
E_i=\frac{\hbar^2k_i^2}{2m}+\frac{1}{2}mv_i^2,\hspace{2mm} i=1,2. 
\label{partial energy}
\end{equation}

\begin{equation}
\langle E\rangle = E_1+E_2. 
\label{total energy of the two solitons}
\end{equation}
